document.addEventListener('DOMContentLoaded', function () {
    const loadMoreButton = document.querySelector('.load-more');
    const loadingSpinner = document.querySelector('.loading-spinner');
    const thatsAllMessage = document.querySelector('.thats-all');
    const cardsSection = document.getElementById('cards-section');

    let visibleCards = 6; // Number of posts to display initially
    const postsPerPage = 6; // Number of posts to load each time
    let startIndex = visibleCards + 1; // Start index for fetching posts

    const blogURL = window.location.origin;
    const blogTitle = "Jinvani"; // Manually set blog title

    function extractImage(content) {
        const tempDiv = document.createElement('div');
        tempDiv.innerHTML = content;
        const img = tempDiv.querySelector('img');
        return img ? img.src : 'https://via.placeholder.com/300x150'; // Default thumbnail
    }

    function stripHTML(html) {
        const doc = new DOMParser().parseFromString(html, 'text/html');
        return doc.body.textContent || "";
    }

    function formatDate(dateString) {
        const date = new Date(dateString);
        const options = { weekday: 'long', day: 'numeric', month: 'long', year: 'numeric' };
        return date.toLocaleDateString('en-US', options);
    }

    function loadMorePosts() {
        loadingSpinner.style.display = 'block';
        loadMoreButton.style.display = 'none';

        const jsonURL = `${blogURL}/feeds/posts/default?alt=json&max-results=${postsPerPage}&start-index=${startIndex}`;

        fetch(jsonURL)
            .then(response => response.json())
            .then(data => {
                const posts = data.feed.entry;
                if (!posts || posts.length === 0) {
                    thatsAllMessage.style.display = 'block';
                    loadingSpinner.style.display = 'none';
                    return;
                }

                posts.forEach(post => {
                    const title = post.title.$t;
                    const link = post.link.find(l => l.rel === "alternate").href;
                    const content = post.content ? post.content.$t : "";
                    const imageSrc = extractImage(content);
                    const snippet = stripHTML(content).substring(0, 150) + '...';
                    const labels = post.category ? post.category.map(cat => `<a class='label' href='${blogURL}/search/label/${cat.term}'>${cat.term}</a>`).join(", ") : "";
                    const publishedDate = formatDate(post.published.$t);

                    const newCard = document.createElement('div');
                    newCard.classList.add('card');
                    newCard.innerHTML = `
                        <!-- Thumbnail -->
                        <div class='card-image'>
                            <a href="${link}">
                                <img alt='Post Thumbnail' src="${imageSrc}"/>
                            </a>
                        </div>
                        <div class='card-content'>
                            <!-- Labels -->
                            <p class='top-labels'>${labels}</p>
                            <!-- Title -->
                            <a href="${link}">
                                <h3>${title}</h3>
                            </a>
                            <!-- Description -->
                            <p class='post-description'>${snippet}</p>
                            <!-- Bottom Meta -->
                            <div class='bottom-meta'>
                                <div class='date-logo'>
                                    <img alt='Logo' src='https://via.placeholder.com/20'/> <!-- Replace with actual logo -->
                                </div>
                                <span class='published-date'>By ${blogTitle} | ${publishedDate}</span>
                            </div>
                        </div>
                    `;

                    cardsSection.appendChild(newCard);
                });

                visibleCards += posts.length;
                startIndex = visibleCards + 1;

                if (posts.length < postsPerPage) {
                    loadMoreButton.style.display = 'none';
                    thatsAllMessage.style.display = 'block';
                } else {
                    loadMoreButton.style.display = 'block';
                }

                loadingSpinner.style.display = 'none';
            })
            .catch(error => {
                console.error("Error loading more posts:", error);
                loadingSpinner.style.display = 'none';
                loadMoreButton.style.display = 'block';
            });
    }

    loadMoreButton.addEventListener('click', loadMorePosts);
});
