    // Fetch Blogger Labels Dynamically
    fetch('/feeds/posts/default?alt=json')
        .then(response => response.json())
        .then(data => {
            let labels = new Set();
            let excludedLabels = ["Depression", "Anxiety", "Stress"]; // Exclude these labels

            // Loop through each post and extract labels
            data.feed.entry.forEach(entry => {
                if (entry.category) {
                    entry.category.forEach(cat => {
                        if (!excludedLabels.includes(cat.term)) { // Exclude specific labels
                            labels.add(cat.term);
                        }
                    });
                }
            });

            // Convert labels to clickable buttons
            let labelHTML = '';
            labels.forEach(label => {
                labelHTML += `<a class="label-box" href="/search/label/${encodeURIComponent(label)}">${label}</a>`;
            });

            document.querySelector('.labels-grid').innerHTML = labelHTML;
        })
        .catch(error => console.log('Error fetching labels:', error));
