// related posts 

function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
            let j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
    }

    function fetchRelatedPosts() {
        let relatedContainer = document.querySelector('.related-posts');
        let currentPostUrl = window.location.href.split('?')[0]; // Remove query parameters from URL

        fetch('/feeds/posts/default?alt=json&max-results=50') // Fetch up to 50 posts for randomness
            .then(response => response.json())
            .then(data => {
                let posts = [];

                data.feed.entry.forEach(entry => {
                    let postTitle = entry.title.$t;
                    let postLink = entry.link.find(link => link.rel === 'alternate').href;

                    // Skip the currently open post
                    if (postLink === currentPostUrl) return;

                    // Fetch proper thumbnail
                    let postImage = 'https://via.placeholder.com/300';
                    if (entry.media$thumbnail) {
                        postImage = entry.media$thumbnail.url.replace(/s72-c/, 's320'); // Load larger image
                    } else if (entry.content.$t.match(/<img.*?src=['"](.*?)['"]/)) {
                        postImage = entry.content.$t.match(/<img.*?src=['"](.*?)['"]/)[1];
                    }

                    posts.push({ title: postTitle, link: postLink, image: postImage });
                });

                // Shuffle the posts array to show a mix of old and new posts randomly
                shuffleArray(posts);

                // Limit to 4 random posts
                posts = posts.slice(0, 4);

                let postHTML = posts.map(post => `
                    <div class="related-post-card">
                        <a href="${post.link}">
                            <img src="${post.image}" alt="${post.title}">
                            <p>${post.title}</p>
                        </a>
                    </div>
                `).join('');

                relatedContainer.innerHTML = postHTML;
            })
            .catch(error => console.log('Error fetching related posts:', error));
    }

    fetchRelatedPosts();

